import React from "react";
import Axios from "axios";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text } from "react-konva";
import Konva from "konva";
import posed from "react-pose";

export default class Upload extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    e.preventDefault();
    let file = e.target.files[0];
    let reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = async () => {
      //console.log({ file });
      //console.log(`base64: ${reader.result}`);

      this.setState({
        file: URL.createObjectURL(file)
      });

      const base64 = reader.result.slice("data:image/jpeg;base64,".length);
      const instance = Axios.create({
        baseURL: "http://localhost:8080",
        headers: {
          "Content-Type": "application/json; charset=utf-8"
        }
      });

      const upload = await instance.post("/facebox/check", { base64 });

      console.log("upload", upload.data);
      const { facesCount, faces } = upload.data;
      if (facesCount > 0) {
        faces.map(face => {
          const { top, left, width, height } = face.rect;
          console.log({ top, left, width, height });
        });
        this.drawCircle();
      }
    };
  }

  render() {
    return (
      <div>
        <input type='file' onChange={this.handleChange} />
        <img src={this.state.file} />
      </div>
    );
  }
}
